    public static FoursquareModel parseData(String jsonString)
    {

        FoursquareModel model = new FoursquareModel();
        try {
            JSONObject object = new JSONObject(jsonString);
            if(!object.isNull("response")){
                JSONObject responseObject = object.getJSONObject("response");
                if(!responseObject.isNull("venues")){
                    JSONArray venuesArray = responseObject.getJSONArray("venues");
                    for(int i=0 ; i<venuesArray.length() ; i++){
                        JSONObject item = venuesArray.getJSONObject(i);
                        if(!item.isNull("name")){
                            model.mName = item.getString("name");
                        }
                        if(!item.isNull("categories")) {
                            JSONArray categories = item.getJSONArray("categories");
                            for (int j = 0; j < categories.length(); j++) {
                                JSONObject category = categories.getJSONObject(j);
                                if (!category.isNull("name")) {
                                    model.mCategory = category.getString("name");
                                }
                                break;
                            }
                        }

                        break;
                    }
                }
            }

        } catch (JSONException e) {
            e.printStackTrace();
        }
        return model;
    }
